import React, { Component } from "react";
import API from "../Utils/API";

class Saved extends Component {
    state = {
        books: {}
    };
    componentDidMount() {
        API.getSavedBooks()
        .then(res => this.setState({ books: res.data}))
        .catch(console.err)
    }
    render() {
    return (
      <div className="container">
        <div className="card" key={books._id}>
          <div className="card-body">
            <div className="row">
              <div className="col">
                <div className="row">
                  <a href={books.title}>
                    <h5 className="card-title">{result.volumeInfo.title}</h5>
                  </a>
                </div>
                <div className="row">
                  <h6>{result.volumeInfo.authors}</h6>
                </div>
              </div>
              <div className="col text-right">
                <img src={result.volumeInfo.imageLinks.smallThumbnail} />
              </div>
            </div>
            <p maxLength="100">{result.volumeInfo.description}</p>
            <button
              className="btn btn-primary"
              onClick={() => props.handleSave(result.id)}
            >
              Save!
            </button>
          </div>
        </div>
      </div>
    );
    }
}
export default Saved;
