import React, { Component } from "react";
import SearchBar from "../Components/SearchBar";
import Results from "../Components/Results";
import API from "../Utils/API";

class SearchPage extends Component{
    state = {
        results: [],
        search: "",
    }
    handleChange = event => {
        const { name, value } = event.target
        this.setState({
            [name] : value
        })
    }
    handleSubmit = () => {
        API.getGoogleBooks(this.state.search).then((results)=>{
            this.setState({
                results: results.data
            })
        })
    }
    handleSave = () => {
        API.saveBook({
            title: this.state.results.volumeInfo.title,
            author: this.state.results.volumeInfo.authors,
            description: results.volumeInfo.description,
            image: results.volumeInfo.imageLinks.smallThumbnail,
            link: results.volumeInfo.infoLink
        })
    }

    render(){
        return(
            <>
            {console.log(this.state.results)}
            <SearchBar handleSubmit={this.handleSubmit} handleChange={this.handleChange} search={this.state.search}/>
            <Results results={this.state.results} handleSave={this.handleSave}/>
            </>
        )
    }
}

export default SearchPage;
