import React, { Component } from "react";
import API from "../Utils/API";

class Saved extends Component {
    state = {
        books: {}
    };
    componentDidMount() {
        API.getSavedBooks()
        .then(res => this.setState({ books: res.data}))
        .catch(console.err)
    }
    render() {
    return (
      <div className="container">
        <div className="card" key={this.state.books._id}>
          <div className="card-body">
            <div className="row">
              <div className="col">
                <div className="row">
                  <a href={this.state.books.link}>
                    <h5 className="card-title">{this.state.books.title}</h5>
                  </a>
                </div>
                <div className="row">
                  <h6>{this.state.books.author}</h6>
                </div>
              </div>
              <div className="col text-right">
                <img src={books.image} />
              </div>
            </div>
            <p maxLength="100">{books.description}</p>
          </div>
        </div>
      </div>
    );
    }
}
export default Saved;
